mapper.select:
select
from comment where id in
#{item}

select

from comment


        AND target_id=#{articleId}


        and auth_status = #{authStatus}


        and reply_status = #{replyStatus}


order by sid ASC, publish_time DESC

select count(*) from comment


        AND target_id=#{articleId}


        and auth_status = #{authStatus}


        and reply_status = #{replyStatus}

mapper.update:
UPDATE comment SET
sid=#{sid,jdbcType=INTEGER}
WHERE id= #{id}

update comment
set is_showed=#{showed}
where id=#{id}
