mapper.select:
select

from msgbroad


        and is_showed = #{authStatus}


        and is_replied = #{replyStatus}


order by sid ASC, publish_time DESC

 select
count(1)
 from msgbroad


         and is_showed = #{authStatus}


         and is_replied = #{replyStatus}

mapper.update:
update msgbroad
set treecode=#{treecode}
where id=#{id}

update msgbroad
set update_time=now()
where id=#{id}

UPDATE msgbroad
SET is_replied = 1
WHERE id=#{id}

UPDATE msgbroad
SET is_showed = #{authStatus}
where id =#{id}

mapper.select:
SELECT
from msgbroad WHERE root_cid IN
#{item}
